import React, { createContext, Component } from 'react';
import { logout, currentUser } from '../';
export const MyContext = createContext();

export class ContextProvider extends Component {
  state = {
    user: {
      username: '',
      id: '',
    },
  };
  componentDidMount = async () => {
    const {
      data: { user },
    } = await currentUser();
    if (user) {
      this.setUser({ user });
    }
  };
  setUser = (user) => {
    this.setState(user);
  };

  removeUser = async () => {
    await logout();
    this.setState({
      user: {
        username: '',
        id: '',
      },
    });
  };

  render() {
    const { setUser, removeUser } = this;
    return (
      <MyContext.Provider value={{ ...this.state, setUser, removeUser }}>
        {this.props.children}
      </MyContext.Provider>
    );
  }
}
